
module Main (main) where

import HTk
import IconBar

main:: IO ()
main =
  do
    main <- initHTk []

    f <- newFrame main []
    pack f [Side AtTop, Fill X]

    ib <- newIconBar f [orient Horizontal]
    pack ib [Side AtLeft]

    img1 <- newImg
    img2 <- openImg
    img3 <- saveImg
    img4 <- printImg
    img5 <- cutImg
    img6 <- copyImg
    img7 <- pasteImg

    b1 <- addButton ib [photo img1]
    b2 <- addButton ib [photo img2]
    b3 <- addButton ib [photo img3]

    addSeparator ib

    b4 <- addButton ib [photo img4]
    b5 <- addButton ib [photo img5]
    b6 <- addButton ib [photo img6]
    b7 <- addButton ib [photo img7]

    ed <- newEditor main [bg "white"]
    pack ed [Side AtBottom, Fill Both, Expand On]

    (htk_destr, _) <- bindSimple main Destroy
    sync (htk_destr)

copyImg = newImage [imgData GIF ""]

cutImg = newImage [imgData GIF ""]

newImg = newImage [imgData GIF ""]

openImg = newImage [imgData GIF ""]

pasteImg = newImage [imgData GIF ""]

printImg = newImage [imgData GIF ""]

saveImg = newImage [imgData GIF ""]

