-- | Basic dialog window and a couple of predefined abstractions.
module DialogWin (
        Dialog,

        dialog,

        createAlertWin,
        createErrorWin,
        createWarningWin,
        createConfirmWin,
	createMessageWin,
        createAlertWin',
        createErrorWin',
        createWarningWin',
        createConfirmWin',
        createMessageWin',
        createDialogWin,
        createDialogWin',

        loadHTkImages,

        questionImg,

        useHTk,
        ) where

import Maybe(fromMaybe)

